//////////////////////////////////////////////////////////////// CONSTRUCTOR
Wegeoo.CityController = function()
{

};
///////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////// GET TOWNS
Wegeoo.CityController.prototype.filterTowns = function(pParameters,pCallback)
{
    if (pParameters instanceof Object)
    {
        var lServiceParameters = {};
        lServiceParameters["filters"] = pParameters;

        var lService = new Wegeoo.Service();
        lService.get(Wegeoo.Service.ROUTE_CITY , lServiceParameters , pCallback);
    }else{
        console.error("pParameters must be an array.");
    }

};
